kubectl get pods --sort-by={.metadata.creationTimestamp} -o go-template --template '{{range .items}}{{.metadata.name}} {{.status.phase}} {{.metadata.creationTimestamp}}{{"\n"}}{{end}}' | awk '$2 == "Running" { print $1 " " $2 " " $3 }' | awk '$3 <= "'$(date -d'now-15 minutes' -Ins --utc | sed 's/+0000/Z/')'" { print $1 }' | grep -v "com-ibm-sparktc-sparkbench-cli-clikickoff.*exec" | xargs --no-run-if-empty kubectl delete pod
